import { useState, useEffect } from 'react';
import { Download } from 'lucide-react';
import { Button } from './Button';

export const InstallPwa = () => {
  const [deferredPrompt, setDeferredPrompt] = useState<any>(null);

  useEffect(() => {
    const handler = (e: any) => {
      e.preventDefault();
      setDeferredPrompt(e);
    };

    window.addEventListener('beforeinstallprompt', handler);

    return () => {
      window.removeEventListener('beforeinstallprompt', handler);
    };
  }, []);

  const handleInstallClick = async () => {
    if (!deferredPrompt) return;

    deferredPrompt.prompt();
    const { outcome } = await deferredPrompt.userChoice;
    
    if (outcome === 'accepted') {
      setDeferredPrompt(null);
    }
  };

  if (!deferredPrompt) return null;

  return (
    <Button 
      onClick={handleInstallClick}
      variant="primary"
      size="sm"
      className="gap-2 animate-bounce"
    >
      <Download size={16} />
      Install App
    </Button>
  );
};

import { createContext, useContext, useState, useEffect, type ReactNode } from 'react';
import { supabase } from '../lib/supabase';
import { showToast } from '../utils/toast';

interface User {
  id: string;
  name: string;
  email: string;
  avatar: string;
  whiskerPoints: number;
  tasksCompleted: number;
  streak: number;
}

interface AuthContextType {
  user: User | null;
  login: (email: string, password?: string) => Promise<boolean>;
  signup: (name: string, email: string, password?: string) => Promise<boolean>;
  loginWithOAuth: (provider: 'google' | 'github') => Promise<void>;
  logout: () => Promise<void>;
  updateProfile: (name: string, avatar: string) => Promise<void>;
  isAuthenticated: boolean;
  loading: boolean;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export const AuthProvider = ({ children }: { children: ReactNode }) => {
  const [user, setUser] = useState<User | null>(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Check active session
    supabase.auth.getSession().then(({ data: { session } }) => {
      if (session?.user) {
        fetchProfile(session.user.id, session.user.email!);
      }
      setLoading(false);
    });

    // Listen for auth changes
    const { data: { subscription } } = supabase.auth.onAuthStateChange((_event, session) => {
      if (session?.user) {
        fetchProfile(session.user.id, session.user.email!);
      } else {
        setUser(null);
        setIsAuthenticated(false);
      }
      setLoading(false);
    });

    return () => subscription.unsubscribe();
  }, []);

  const fetchProfile = async (userId: string, email: string) => {
    try {
      const { data, error } = await supabase
        .from('profiles')
        .select('*')
        .eq('id', userId)
        .single();

      if (error) throw error;

      if (data) {
        setUser({
          id: data.id,
          name: data.name || email.split('@')[0],
          email: data.email || email,
          avatar: data.avatar || '­ƒÉ▒',
          whiskerPoints: data.whisker_points || 0,
          tasksCompleted: data.tasks_completed || 0,
          streak: data.streak || 0
        });
        setIsAuthenticated(true);
      }
    } catch (error) {
      console.error('Error fetching profile:', error);
    }
  };

  const login = async (email: string, password?: string): Promise<boolean> => {
    try {
      if (password) {
        const { error } = await supabase.auth.signInWithPassword({
          email,
          password,
        });
        if (error) throw error;
        showToast.success('Welcome back! ­ƒÉ▒');
        return true;
      } else {
        const { error } = await supabase.auth.signInWithOtp({
          email,
          options: {
            emailRedirectTo: window.location.origin,
          },
        });
        if (error) throw error;
        showToast.success('Magic link sent! Check your email ­ƒôº');
        return false; // Don't redirect for magic link, user needs to check email
      }
    } catch (error: any) {
      showToast.error(error.message);
      return false;
    }
  };

  const signup = async (name: string, email: string, password?: string): Promise<boolean> => {
    try {
      const { error } = await supabase.auth.signUp({
        email,
        password: password || 'temporary-password-placeholder',
        options: {
          data: {
            name,
          },
        },
      });
      if (error) throw error;
      showToast.success('Account created! Please check your email to verify. ­ƒôº');
      return true; // Can redirect or stay, usually stay for verification
    } catch (error: any) {
      showToast.error(error.message);
      return false;
    }
  };

  const loginWithOAuth = async (provider: 'google' | 'github') => {
    try {
      const { error } = await supabase.auth.signInWithOAuth({
        provider,
        options: {
          redirectTo: `${window.location.origin}/dashboard`,
        },
      });
      if (error) throw error;
    } catch (error: any) {
      showToast.error(error.message);
    }
  };

  const logout = async () => {
    await supabase.auth.signOut();
    setUser(null);
    setIsAuthenticated(false);
    showToast.success('See you later! ­ƒæï');
  };

  const updateProfile = async (name: string, avatar: string) => {
    if (!user) return;

    try {
      const { error } = await supabase
        .from('profiles')
        .update({ name, avatar })
        .eq('id', user.id);

      if (error) throw error;

      setUser({ ...user, name, avatar });
    } catch (error: any) {
      showToast.error('Failed to update profile');
    }
  };

  return (
    <AuthContext.Provider value={{ user, isAuthenticated, loading, login, signup, loginWithOAuth, logout, updateProfile }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) throw new Error('useAuth must be used within AuthProvider');
  return context;
};

import { createContext, useContext, useState, useEffect, type ReactNode } from 'react';

type ThemeColor = 'pink' | 'blue' | 'green' | 'purple' | 'orange';
type Language = 'en' | 'es' | 'fr' | 'ja';

interface SettingsContextType {
  themeColor: ThemeColor;
  setThemeColor: (color: ThemeColor) => void;
  soundEnabled: boolean;
  setSoundEnabled: (enabled: boolean) => void;
  language: Language;
  setLanguage: (lang: Language) => void;
}

const SettingsContext = createContext<SettingsContextType | undefined>(undefined);

export const SettingsProvider = ({ children }: { children: ReactNode }) => {
  const [themeColor, setThemeColor] = useState<ThemeColor>(() => {
    return (localStorage.getItem('kitty-theme-color') as ThemeColor) || 'pink';
  });

  const [soundEnabled, setSoundEnabled] = useState(() => {
    const saved = localStorage.getItem('kitty-sound-enabled');
    return saved ? JSON.parse(saved) : true;
  });

  const [language, setLanguage] = useState<Language>(() => {
    return (localStorage.getItem('kitty-language') as Language) || 'en';
  });

  useEffect(() => {
    localStorage.setItem('kitty-theme-color', themeColor);
    document.documentElement.setAttribute('data-theme', themeColor);
  }, [themeColor]);

  useEffect(() => {
    localStorage.setItem('kitty-sound-enabled', JSON.stringify(soundEnabled));
  }, [soundEnabled]);

  useEffect(() => {
    localStorage.setItem('kitty-language', language);
  }, [language]);

  return (
    <SettingsContext.Provider value={{
      themeColor,
      setThemeColor,
      soundEnabled,
      setSoundEnabled,
      language,
      setLanguage
    }}>
      {children}
    </SettingsContext.Provider>
  );
};

export const useSettings = () => {
  const context = useContext(SettingsContext);
  if (!context) throw new Error('useSettings must be used within SettingsProvider');
  return context;
};

import React, { createContext, useContext, useEffect, useState } from 'react';

type Theme = 'light' | 'dark';

interface ThemeContextType {
  theme: Theme;
  toggleTheme: () => void;
}

const ThemeContext = createContext<ThemeContextType | undefined>(undefined);

export const ThemeProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [theme, setTheme] = useState<Theme>(() => {
    const saved = localStorage.getItem('kitty-theme');
    return (saved as Theme) || 'light';
  });

  useEffect(() => {
    localStorage.setItem('kitty-theme', theme);
    document.documentElement.classList.toggle('dark', theme === 'dark');
  }, [theme]);

  const toggleTheme = () => {
    setTheme(prev => prev === 'light' ? 'dark' : 'light');
  };

  return (
    <ThemeContext.Provider value={{ theme, toggleTheme }}>
      {children}
    </ThemeContext.Provider>
  );
};

export const useTheme = () => {
  const context = useContext(ThemeContext);
  if (!context) throw new Error('useTheme must be used within ThemeProvider');
  return context;
};

import { useSettings } from '../contexts/SettingsContext';

type SoundType = 'click' | 'success' | 'delete' | 'pop';

export const useSound = () => {
  const { soundEnabled } = useSettings();

  const playSound = (type: SoundType) => {
    if (!soundEnabled) return;

    // In a real app, these would be actual audio files.
    // For this demo, we'll use simple generated beeps if possible, 
    // or just log to console to simulate.
    // To make it "real" without external assets, we can use the Web Audio API or small data URIs.
    // For simplicity and reliability in this environment, we'll use short data URIs for simple beeps.
    
    // Let's use a simple oscillator for immediate feedback without assets
    try {
      const AudioContext = window.AudioContext || (window as any).webkitAudioContext;
      if (AudioContext) {
        const ctx = new AudioContext();
        const osc = ctx.createOscillator();
        const gain = ctx.createGain();

        osc.connect(gain);
        gain.connect(ctx.destination);

        const now = ctx.currentTime;
        
        switch (type) {
          case 'click':
            osc.frequency.setValueAtTime(600, now);
            gain.gain.setValueAtTime(0.1, now);
            osc.start(now);
            osc.stop(now + 0.05);
            break;
          case 'success':
            osc.frequency.setValueAtTime(400, now);
            osc.frequency.linearRampToValueAtTime(800, now + 0.1);
            gain.gain.setValueAtTime(0.1, now);
            gain.gain.linearRampToValueAtTime(0, now + 0.3);
            osc.start(now);
            osc.stop(now + 0.3);
            break;
          case 'delete':
            osc.frequency.setValueAtTime(300, now);
            osc.frequency.linearRampToValueAtTime(100, now + 0.1);
            gain.gain.setValueAtTime(0.1, now);
            gain.gain.linearRampToValueAtTime(0, now + 0.2);
            osc.start(now);
            osc.stop(now + 0.2);
            break;
          case 'pop':
            osc.frequency.setValueAtTime(800, now);
            gain.gain.setValueAtTime(0.05, now);
            osc.start(now);
            osc.stop(now + 0.03);
            break;
        }
      }
    } catch (e) {
      console.error('Audio play failed', e);
    }
  };

  return { playSound };
};

import { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Plus, Trash2, CheckCircle2, Circle, Calendar, Sparkles, Share2 } from 'lucide-react';
import { Navbar } from '../sections/Navbar';
import { Footer } from '../sections/Footer';
import { Card } from '../components/Card';
import { Button } from '../components/Button';
import { AddTaskModal } from '../components/AddTaskModal';
import { useAuth } from '../contexts/AuthContext';
import { useSound } from '../hooks/useSound';
import { supabase } from '../lib/supabase';
import { showToast } from '../utils/toast';
import confetti from 'canvas-confetti';

interface Task {
  id: number;
  title: string;
  completed: boolean;
  category: string;
  created_at?: string;
}

export const Dashboard = () => {
  const { user } = useAuth();
  const { playSound } = useSound();
  const [tasks, setTasks] = useState<Task[]>([]);
  const [isAddModalOpen, setIsAddModalOpen] = useState(false);
  const [selectedMood, setSelectedMood] = useState('­ƒÿ©');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchTasks();
  }, [user]);

  const fetchTasks = async () => {
    if (!user) return;
    try {
      const { data, error } = await supabase
        .from('tasks')
        .select('*')
        .order('created_at', { ascending: false });

      if (error) throw error;
      setTasks(data || []);
    } catch (error) {
      console.error('Error fetching tasks:', error);
    } finally {
      setLoading(false);
    }
  };

  const addTask = async (title: string, category: string) => {
    if (!user) return;
    try {
      const { data, error } = await supabase
        .from('tasks')
        .insert([{ title, category, user_id: user.id }])
        .select()
        .single();

      if (error) throw error;

      setTasks([data, ...tasks]);
      playSound('pop');
      showToast.success('Task added! ­ƒôØ');
      setIsAddModalOpen(false);
    } catch (error: any) {
      showToast.error('Failed to add task');
    }
  };

  const toggleTask = async (id: number) => {
    const task = tasks.find(t => t.id === id);
    if (!task) return;

    try {
      const { error } = await supabase
        .from('tasks')
        .update({ completed: !task.completed })
        .eq('id', id);

      if (error) throw error;

      setTasks(tasks.map(t => 
        t.id === id ? { ...t, completed: !t.completed } : t
      ));

      if (!task.completed) {
        playSound('success');
        confetti({
          particleCount: 100,
          spread: 70,
          origin: { y: 0.6 },
          colors: ['#F472B6', '#60A5FA', '#34D399']
        });
      }
    } catch (error) {
      showToast.error('Failed to update task');
    }
  };

  const deleteTask = async (id: number) => {
    try {
      const { error } = await supabase
        .from('tasks')
        .delete()
        .eq('id', id);

      if (error) throw error;

      setTasks(tasks.filter(t => t.id !== id));
      playSound('delete');
      showToast.success('Task deleted! ­ƒùæ´©Å');
    } catch (error) {
      showToast.error('Failed to delete task');
    }
  };

  const moods = ['­ƒÿ©', '­ƒÿ┐', '­ƒÿ╗', '­ƒÿ╝', '­ƒÿ¢'];

  return (
    <div className="min-h-screen bg-kitty-cream dark:bg-gray-900 transition-colors duration-300">
      <Navbar />
      
      <main className="pt-24 pb-20 px-4 max-w-7xl mx-auto">
        <div className="grid md:grid-cols-3 gap-8">
          {/* Left Column - Welcome & Stats */}
          <div className="md:col-span-2 space-y-8">
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
            >
              <h1 className="text-3xl font-bold text-kitty-text dark:text-white mb-2">
                Good Meow-ning, {user?.name}! ÔÿÇ´©Å
              </h1>
              <p className="text-gray-500 dark:text-gray-400">
                You have {tasks.filter(t => !t.completed).length} pending tasks today. Let's crush them!
              </p>
            </motion.div>

            {/* Mood Tracker */}
            <Card className="p-6 dark:bg-gray-800">
              <h3 className="text-lg font-bold text-kitty-text dark:text-white mb-4 flex items-center gap-2">
                <Sparkles size={20} className="text-yellow-400" />
                How are you feline today?
              </h3>
              <div className="flex gap-4 overflow-x-auto pb-2 scrollbar-hide -mx-2 px-2">
                {moods.map((mood) => (
                  <button
                    key={mood}
                    onClick={() => setSelectedMood(mood)}
                    className={`text-4xl p-2 rounded-2xl transition-all hover:scale-110 flex-shrink-0 ${
                      selectedMood === mood ? 'bg-kitty-pink/20 ring-2 ring-kitty-pink' : 'hover:bg-gray-100 dark:hover:bg-gray-700'
                    }`}
                  >
                    {mood}
                  </button>
                ))}
              </div>
            </Card>

            {/* Tasks List */}
            <div className="space-y-4">
              <div className="flex flex-col sm:flex-row items-start sm:items-center justify-between gap-4">
                <h2 className="text-xl font-bold text-kitty-text dark:text-white">My Tasks</h2>
                <div className="flex gap-2 w-full sm:w-auto">
                  <Button 
                    onClick={() => {
                      const pendingTasks = tasks.filter(t => !t.completed);
                      if (pendingTasks.length > 0) {
                        const randomTask = pendingTasks[Math.floor(Math.random() * pendingTasks.length)];
                        showToast.success(`Ô£¿ Magic Suggestion: ${randomTask.title}`);
                        playSound('pop');
                      } else {
                        showToast.success('All caught up! ­ƒÄë');
                      }
                    }}
                    size="sm" 
                    variant="secondary"
                    className="gap-2 flex-1 sm:flex-none"
                  >
                    <Sparkles size={18} /> <span className="truncate">Magic Suggest</span>
                  </Button>
                  <Button onClick={() => setIsAddModalOpen(true)} size="sm" className="gap-2 flex-1 sm:flex-none">
                    <Plus size={18} /> <span className="whitespace-nowrap">Add Task</span>
                  </Button>
                </div>
              </div>

              {loading ? (
                <div className="text-center py-10 text-gray-500">Loading tasks... ­ƒÉ▒</div>
              ) : tasks.length === 0 ? (
                <Card className="p-8 text-center dark:bg-gray-800">
                  <div className="text-6xl mb-4">­ƒÆñ</div>
                  <h3 className="text-lg font-bold text-kitty-text dark:text-white mb-2">No tasks yet</h3>
                  <p className="text-gray-500 dark:text-gray-400 mb-4">Time for a cat nap, or add a new task!</p>
                  <Button onClick={() => setIsAddModalOpen(true)} variant="outline">Create Task</Button>
                </Card>
              ) : (
                <div className="space-y-3">
                  <AnimatePresence mode="popLayout">
                    {tasks.map((task) => (
                      <motion.div
                        key={task.id}
                        initial={{ opacity: 0, x: -20 }}
                        animate={{ opacity: 1, x: 0 }}
                        exit={{ opacity: 0, x: 20 }}
                        layout
                      >
                        <Card className={`p-4 flex items-center gap-4 group transition-colors ${
                          task.completed ? 'bg-gray-50 dark:bg-gray-800/50' : 'bg-white dark:bg-gray-800'
                        }`}>
                          <button
                            onClick={() => toggleTask(task.id)}
                            className={`p-2 rounded-full transition-colors ${
                              task.completed ? 'text-green-500 bg-green-100 dark:bg-green-900/30' : 'text-gray-300 hover:text-kitty-pink'
                            }`}
                          >
                            {task.completed ? <CheckCircle2 size={24} /> : <Circle size={24} />}
                          </button>
                          
                          <div className="flex-1">
                            <h3 className={`font-medium transition-all ${
                              task.completed ? 'text-gray-400 line-through' : 'text-kitty-text dark:text-white'
                            }`}>
                              {task.title}
                            </h3>
                            <span className="text-xs text-gray-400 bg-gray-100 dark:bg-gray-700 px-2 py-1 rounded-full mt-1 inline-block">
                              {task.category}
                            </span>
                          </div>

                          <button
                            onClick={() => deleteTask(task.id)}
                            className="p-2 text-gray-400 hover:text-red-500 opacity-0 group-hover:opacity-100 transition-all"
                          >
                            <Trash2 size={18} />
                          </button>

                          <button
                            onClick={() => {
                              if (navigator.share) {
                                navigator.share({
                                  title: 'Purrfect Task',
                                  text: `Check out this task: ${task.title}`,
                                  url: window.location.href,
                                });
                              } else {
                                navigator.clipboard.writeText(`Task: ${task.title}`);
                                showToast.success('Task copied to clipboard! ­ƒôï');
                              }
                            }}
                            className="p-2 text-gray-400 hover:text-blue-500 opacity-0 group-hover:opacity-100 transition-all"
                          >
                            <Share2 size={18} />
                          </button>
                        </Card>
                      </motion.div>
                    ))}
                  </AnimatePresence>
                </div>
              )}
            </div>
          </div>

          {/* Right Column - Calendar & Progress */}
          <div className="space-y-8">
            <Card className="p-6 dark:bg-gray-800">
              <h3 className="text-lg font-bold text-kitty-text dark:text-white mb-4 flex items-center gap-2">
                <Calendar size={20} className="text-kitty-pink" />
                Today's Progress
              </h3>
              <div className="flex items-center justify-center py-4">
                <div className="relative w-32 h-32">
                  <svg className="w-full h-full transform -rotate-90">
                    <circle
                      cx="64"
                      cy="64"
                      r="56"
                      stroke="currentColor"
                      strokeWidth="12"
                      fill="none"
                      className="text-gray-100 dark:text-gray-700"
                    />
                    <circle
                      cx="64"
                      cy="64"
                      r="56"
                      stroke="currentColor"
                      strokeWidth="12"
                      fill="none"
                      strokeDasharray={351.86}
                      strokeDashoffset={351.86 - (351.86 * (tasks.length > 0 ? tasks.filter(t => t.completed).length / tasks.length : 0))}
                      className="text-kitty-pink transition-all duration-1000 ease-out"
                      strokeLinecap="round"
                    />
                  </svg>
                  <div className="absolute inset-0 flex items-center justify-center flex-col">
                    <span className="text-3xl font-bold text-kitty-text dark:text-white">
                      {tasks.length > 0 ? Math.round((tasks.filter(t => t.completed).length / tasks.length) * 100) : 0}%
                    </span>
                  </div>
                </div>
              </div>
            </Card>
          </div>
        </div>
      </main>

      <AddTaskModal
        isOpen={isAddModalOpen}
        onClose={() => setIsAddModalOpen(false)}
        onAdd={addTask}
      />
      <Footer />
    </div>
  );
};

import { motion } from 'framer-motion';
import { Navbar } from '../sections/Navbar';
import { Footer } from '../sections/Footer';
import { Download, Monitor, Smartphone, Tablet, Check } from 'lucide-react';
import { Button } from '../components/Button';

export const DownloadPage = () => {
  return (
    <div className="min-h-screen bg-kitty-cream dark:bg-gray-900 transition-colors duration-300">
      <Navbar />
      
      <main className="pt-32 pb-20 px-4 max-w-7xl mx-auto">
        <div className="flex flex-col lg:flex-row items-center gap-12 lg:gap-20">
          <motion.div 
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            className="flex-1 w-full max-w-xl"
          >
            <h1 className="text-4xl md:text-5xl lg:text-6xl font-extrabold mb-6 leading-tight text-center lg:text-left">
              Get Purrfect
              <span className="block bg-gradient-to-r from-kitty-pink via-purple-500 to-pink-600 bg-clip-text text-transparent mt-2">
                Everywhere
              </span>
            </h1>
            <p className="text-lg md:text-xl text-gray-600 dark:text-gray-300 mb-8 leading-relaxed text-center lg:text-left">
              Experience the cuteness on all your devices. Install Purrfect Tasks as a Progressive Web App (PWA) for a native-like experience.
            </p>
            
            <div className="flex flex-wrap justify-center lg:justify-start gap-4 mb-12">
              <Button 
                size="lg" 
                className="gap-2 shadow-lg shadow-kitty-pink/30 w-full sm:w-auto"
                onClick={() => {
                  // @ts-ignore
                  const promptEvent = window.deferredPrompt;
                  if (promptEvent) {
                    promptEvent.prompt();
                    promptEvent.userChoice.then((choiceResult: any) => {
                      if (choiceResult.outcome === 'accepted') {
                        console.log('User accepted the install prompt');
                      }
                      // @ts-ignore
                      window.deferredPrompt = null;
                    });
                  } else {
                    alert('App is already installed or not supported in this browser!');
                  }
                }}
              >
                <Download size={20} />
                Install App
              </Button>
              <Button variant="outline" size="lg" className="gap-2 w-full sm:w-auto">
                View Web Version
              </Button>
            </div>

            <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
              <h3 className="font-bold text-gray-900 dark:text-white mb-2 col-span-full text-center lg:text-left">Why install?</h3>
